import { notifications } from "@/services/enhancedNotificationService";
import { unifiedNotifications } from "@/services/unifiedNotificationService";

interface EmergencyError {
  type:
    | "location"
    | "connectivity"
    | "battery"
    | "permission"
    | "system"
    | "service";
  severity: "low" | "medium" | "high" | "critical";
  message: string;
  context?: string;
  timestamp: Date;
  fallbackActions?: string[];
}

class EmergencyErrorHandler {
  private errorQueue: EmergencyError[] = [];
  private isHandling = false;

  async handleEmergencyError(error: Partial<EmergencyError>): Promise<boolean> {
    const emergencyError: EmergencyError = {
      type: error.type || "system",
      severity: error.severity || "medium",
      message: error.message || "Unknown emergency error",
      context: error.context,
      timestamp: new Date(),
      fallbackActions: error.fallbackActions || [],
    };

    this.errorQueue.push(emergencyError);

    if (!this.isHandling) {
      return this.processErrorQueue();
    }

    return false;
  }

  private async processErrorQueue(): Promise<boolean> {
    this.isHandling = true;
    let success = false;

    try {
      while (this.errorQueue.length > 0) {
        const error = this.errorQueue.shift();
        if (error) {
          success = (await this.handleSingleError(error)) || success;
        }
      }
    } catch (err) {
      console.error("Emergency error handler failed:", err);
    } finally {
      this.isHandling = false;
    }

    return success;
  }

  private async handleSingleError(error: EmergencyError): Promise<boolean> {
    console.error("üö® Emergency Error:", error);

    switch (error.type) {
      case "location":
        return this.handleLocationError(error);
      case "connectivity":
        return this.handleConnectivityError(error);
      case "battery":
        return this.handleBatteryError(error);
      case "permission":
        return this.handlePermissionError(error);
      case "service":
        return this.handleServiceError(error);
      default:
        return this.handleSystemError(error);
    }
  }

  private async handleLocationError(error: EmergencyError): Promise<boolean> {
    switch (error.severity) {
      case "critical":
        // GPS completely failed during emergency
        unifiedNotifications.error("üö® CRITICAL: Location Service Failed", {
          message:
            "Emergency location cannot be determined. Using last known position.",
          persistent: true,
        });

        // Try to get last known location from storage
        const lastLocation = localStorage.getItem("lastKnownLocation");
        if (lastLocation) {
          try {
            const location = JSON.parse(lastLocation);
            window.dispatchEvent(
              new CustomEvent("emergencyLocationFallback", {
                detail: location,
              }),
            );
            return true;
          } catch (e) {
            console.error("Failed to parse last known location");
          }
        }

        // Show manual location input as last resort
        window.dispatchEvent(new CustomEvent("showEmergencyLocationInput"));
        return false;

      case "high":
        unifiedNotifications.warning("‚ö†Ô∏è Location Accuracy Reduced", {
          message: "GPS signal weak. Using network-based location.",
        });
        return this.fallbackToNetworkLocation();

      default:
        notifications.warning({
          title: "Location Service Issue",
          description: error.message,
        });
        return true;
    }
  }

  private async handleConnectivityError(
    error: EmergencyError,
  ): Promise<boolean> {
    switch (error.severity) {
      case "critical":
        unifiedNotifications.error("üî¥ OFFLINE MODE", {
          message:
            "Emergency features now running offline. SOS will use SMS/Call.",
          persistent: true,
        });

        // Enable offline emergency mode
        window.dispatchEvent(new CustomEvent("enableOfflineEmergencyMode"));
        return this.enableOfflineEmergencyMode();

      case "high":
        unifiedNotifications.warning("üì∂ Poor Connection", {
          message: "Limited connectivity. Emergency features may be delayed.",
        });
        return this.enableReducedConnectivityMode();

      default:
        notifications.info({
          title: "Connectivity Issue",
          description: "Some features may be limited.",
        });
        return true;
    }
  }

  private async handleBatteryError(error: EmergencyError): Promise<boolean> {
    switch (error.severity) {
      case "critical":
        unifiedNotifications.error("üîã CRITICAL BATTERY", {
          message: "Battery critically low. Emergency features prioritized.",
          persistent: true,
        });

        // Force ultra-low power mode
        window.dispatchEvent(new CustomEvent("enableCriticalBatteryMode"));
        return this.enableCriticalBatteryMode();

      case "high":
        unifiedNotifications.warning("üîã Low Battery", {
          message: "Battery saver enabled. Non-essential features disabled.",
        });
        return true;

      default:
        return true;
    }
  }

  private async handlePermissionError(error: EmergencyError): Promise<boolean> {
    switch (error.severity) {
      case "critical":
        unifiedNotifications.error("üö´ PERMISSION DENIED", {
          message: "Critical permissions missing. Emergency features limited.",
          persistent: true,
        });

        // Show permission recovery guide
        window.dispatchEvent(new CustomEvent("showPermissionRecovery"));
        return false;

      default:
        unifiedNotifications.warning("Permission Required", {
          message: error.message,
        });
        return false;
    }
  }

  private async handleServiceError(error: EmergencyError): Promise<boolean> {
    unifiedNotifications.warning("Service Issue", {
      message: `${error.message}. Attempting recovery...`,
    });

    // Attempt service recovery
    window.dispatchEvent(
      new CustomEvent("attemptServiceRecovery", {
        detail: { context: error.context },
      }),
    );

    return true;
  }

  private async handleSystemError(error: EmergencyError): Promise<boolean> {
    if (error.severity === "critical") {
      unifiedNotifications.error("System Error", {
        message: "Critical system error. Emergency fallbacks activated.",
        persistent: true,
      });

      // Activate all emergency fallbacks
      window.dispatchEvent(new CustomEvent("activateEmergencyFallbacks"));
    } else {
      notifications.error({
        title: "System Error",
        description: error.message,
      });
    }

    return error.severity !== "critical";
  }

  private async fallbackToNetworkLocation(): Promise<boolean> {
    try {
      // Try IP-based location as fallback
      const response = await fetch("https://ipapi.co/json/");
      const data = await response.json();

      if (data.latitude && data.longitude) {
        window.dispatchEvent(
          new CustomEvent("emergencyLocationFallback", {
            detail: {
              latitude: data.latitude,
              longitude: data.longitude,
              accuracy: 10000, // Very rough accuracy
              source: "ip",
            },
          }),
        );
        return true;
      }
    } catch (e) {
      console.error("Network location fallback failed");
    }
    return false;
  }

  private async enableOfflineEmergencyMode(): Promise<boolean> {
    try {
      // Store offline emergency data
      const offlineData = {
        enabled: true,
        timestamp: Date.now(),
        lastKnownLocation: localStorage.getItem("lastKnownLocation"),
        emergencyContacts: localStorage.getItem("emergencyContacts"),
      };

      localStorage.setItem("offlineEmergencyMode", JSON.stringify(offlineData));

      // Disable non-essential features
      document.body.classList.add("offline-emergency-mode");

      return true;
    } catch (e) {
      console.error("Failed to enable offline emergency mode");
      return false;
    }
  }

  private async enableReducedConnectivityMode(): Promise<boolean> {
    try {
      // Reduce update frequencies and optimize for poor connection
      window.dispatchEvent(new CustomEvent("enableReducedConnectivity"));
      document.body.classList.add("reduced-connectivity-mode");
      return true;
    } catch (e) {
      return false;
    }
  }

  private async enableCriticalBatteryMode(): Promise<boolean> {
    try {
      // Disable all non-emergency features
      document.body.classList.add("critical-battery-emergency");

      // Stop all background processes except emergency
      window.dispatchEvent(new CustomEvent("stopNonEmergencyProcesses"));

      return true;
    } catch (e) {
      return false;
    }
  }

  // Emergency contact methods when all else fails
  showEmergencyContactMethods() {
    const emergencyActions = [
      "üìû Dial 911 directly",
      "üì± Send emergency SMS",
      "üîä Activate loud alarm",
      "üìç Share last known location",
    ];

    unifiedNotifications.error("Emergency Contact Methods", {
      message: emergencyActions.join(" ‚Ä¢ "),
      persistent: true,
    });
  }

  // Quick emergency actions
  async executeEmergencyFallback(action: string): Promise<boolean> {
    switch (action) {
      case "call_911":
        window.open("tel:911", "_self");
        return true;

      case "send_sos_sms":
        const emergencyContacts = localStorage.getItem("emergencyContacts");
        if (emergencyContacts) {
          try {
            const contacts = JSON.parse(emergencyContacts);
            const message =
              "ÔøΩÔøΩÔøΩÔøΩ EMERGENCY - I need immediate assistance. Please contact emergency services.";
            contacts.forEach((contact: any) => {
              if (contact.phone) {
                window.open(
                  `sms:${contact.phone}?body=${encodeURIComponent(message)}`,
                  "_blank",
                );
              }
            });
            return true;
          } catch (e) {
            console.error("Failed to send emergency SMS");
          }
        }
        return false;

      case "activate_alarm":
        // Play emergency sound if available
        try {
          const audio = new Audio();
          audio.src =
            "data:audio/wav;base64,";
          audio.volume = 0.3;
          audio.play().catch(() => {
            // Fallback: use device vibration if available
            if (navigator.vibrate) {
              navigator.vibrate([200, 100, 200, 100, 200]);
            }
          });
          return true;
        } catch (e) {
          console.error("Failed to activate emergency alarm");
          return false;
        }

      case "share_location":
        const lastLocation = localStorage.getItem("lastKnownLocation");
        if (lastLocation) {
          try {
            const location = JSON.parse(lastLocation);
            const url = `https://maps.google.com/?q=${location.latitude},${location.longitude}`;
            navigator.clipboard?.writeText(url);
            window.open(`sms:?body=Emergency location: ${url}`, "_blank");
            return true;
          } catch (e) {
            console.error("Failed to share location");
          }
        }
        return false;

      default:
        return false;
    }
  }

  // Clear all emergency states
  clearEmergencyStates() {
    localStorage.removeItem("offlineEmergencyMode");
    document.body.classList.remove(
      "offline-emergency-mode",
      "reduced-connectivity-mode",
      "critical-battery-emergency",
    );
    this.errorQueue = [];
  }
}

export const emergencyErrorHandler = new EmergencyErrorHandler();
export type { EmergencyError };
